function findAddValidRadio(event){ 
  setTimeout( function(){
    angular.forEach(angular.element('input[type="radio"]'), function(value, key){
      var a = angular.element(value); var  stato=null;    
      if(a.hasClass('ng-invalid-required')){      stato = 'invalid';    }
      if(a.hasClass('ng-valid-required')){      stato = 'valid';     }

//elm //<div class="form-inline">
      //  <label class="ui-radio">
//a   //    <input type="radio" ng-required="true" value="" ng-model=""><span></span>
      //  </label>
      //  ... ...
      //</div>

      var elm = a.parent().parent();//ui-radio , form-inline
      if(stato && !elm.hasClass('has-valid-required') && !elm.hasClass('has-invalid-required')){
        elm.addClass('has-invalid-required').on('click', function() {
          findAddValidRadio();
        });
      }
      if(stato=='valid' && !elm.hasClass('has-valid-required')){
        elm.removeClass('has-invalid-required').addClass('has-valid-required');
      } 
      if(stato=='invalid' && !elm.hasClass('has-invalid-required')){
        elm.removeClass('has-valid-required').addClass('has-invalid-required');
      }
    });
  },50 );
}
findAddValidRadio();

