var calcoloTotale=function(){
  var orientamento=formData.totaleOrientamento;
  var memoria=formData.totaleMemoria;
  var atenzioneC=formData.totaleAtenzioneCalcolo;
  var ricordo=formData.totaleRicordo;
  var linguaggio=formData.totaleLinguaggio;
  var abilitaP=formData.abilitaPrassicototale;
  var valutazione=formData.valutazionetotale;

  var complessivoCalcolato=orientamento+memoria+atenzioneC+ricordo+linguaggio+abilitaP;
  var complessivoAggiustato=valutazione+complessivoCalcolato;
  //print
  if(!formData.punteggioComplessivo){
    formData.punteggioComplessivo={};
  }
  formData.punteggioComplessivo.calcolato=complessivoCalcolato;
  formData.punteggioComplessivo.aggiustato=complessivoAggiustato;

};
registerFunction("calcoloTotale",calcoloTotale);

