javascript:
//Scala per l'agitazione di Cohen-Mansfield (CMAI)
var calcCMAI = function(group){
  var elm, somma = null;
  for(var i in group){
    elm = group[i];
    if(elm && elm.score >= 0){
      somma = somma + elm.score;
    }
  }
  return somma;
};
registerFunction("calcCMAI",calcCMAI);


//ng-click="selectedRadio($event)"
var selectedRadio = function(event){
  if(event.target.localName ==="input" && event.target.type === "radio"){
    formData.scoreScaleCMAI = calcCMAI(formData);
  }
};
registerFunction("selectedRadio",selectedRadio);

