javascript:
var detectedNA = function (event){
  var currentObj = formData, array, objString;
  objString = event.target.getAttribute('ng-model'); //"formData.deliri.detected"
  array = objString.split('.');                      //array=> ['formData','deliri','detected']
  var e = array.indexOf( 'formData' );           
  if ( e !== -1 ) {  array.splice( e, 1 );  }        //array=> ['deliri','detected']
  e = array.indexOf( 'detected' );        
  if ( e !== -1 ) {  array.splice( e, 1 );  }        //array=> ['deliri']
  for(var i in array){
    currentObj = currentObj[array[i]];
  }
  var elm = currentObj;  //formData.deliri={...}
  if(elm.frequenza){  elm.frequenza=null;  }
  if(elm.gravita){  elm.gravita=null;  }
  if(elm.npi || elm.npi!=null ){  elm.npi=null;  }
  if(elm.distress){  elm.distress=null;  }

  calcoloResult();
};
registerFunction("detectedNA",detectedNA);

var calcNPI = function (group){
  var elm, somma = null;
  for(var i in group){
    elm = group[i];
    if(elm && elm.frequenza && elm.gravita && !elm.detected){
      elm.npi = (elm.frequenza.score * elm.gravita.score);
    }
  }
};
registerFunction("calcNPI",calcNPI);


var SommaNPI = function (group){
  var elm, somma = null;
  for(var i in group){
    elm = group[i];
    if(elm && elm.npi != null ){  
      somma = somma + elm.npi;
    }
  }
  return somma;
};
registerFunction("SommaNPI",SommaNPI);


var SommaDistress = function (group){
  var elm, somma = null;
  for(var i in group){
    elm = group[i]; 
    if(elm && elm.distress && !elm.detected){
      somma = somma +  elm.distress.score; 
    }
  }
  return somma;
};
registerFunction("SommaDistress",SommaDistress);

var calcoloResult =function(){
  calcNPI(formData);
  formData.sommaNPI = SommaNPI(formData);
  formData.sommaDistess = SommaDistress(formData);

  var resultFinal=null;
  if(formData.sommaNPI != null && formData.sommaDistess != null)
    resultFinal = formData.sommaNPI + formData.sommaDistess;

  formData.npiFinale = resultFinal;
};

registerFunction("calcoloResult",calcoloResult);



var selectedRadio = function(event){
  console.log(event);
  if((event.target.localName ==="input" && event.target.type === "radio")){
    calcoloResult();
  }
};
registerFunction("selectedRadio",selectedRadio);

